mapper.select:
select COUNT(1) > 0
from forum_likes
where comment_id = #{commentId} and user_id = #{userId}

choose.when:
select state
from forums
where forum_id = #{forumId}

choose.otherwise:
select state
from forum_comments
where comment_id = #{parentCommentId}

mapper.select:
select state
from forum_comments
where comment_id = #{commentId}

mapper.update:
update forums



            comment_count = comment_count + 1, update_time = #{updateTime}



            comment_count = comment_count - 1, update_time = #{updateTime}



where forum_id = #{forumId}

update forum_comments



            comment_count = comment_count + 1, update_time = #{updateTime}



            comment_count = comment_count - 1, update_time = #{updateTime}



where comment_id = #{parentCommentId}

mapper.select:
select user_id, forum_id, parent_comment_id, ancestor_comment_id, state
from forum_comments
where comment_id = #{commentId}

mapper.update:
update forum_comments
set state = 'Deleted', update_time = #{updateTime}


        forum_id = #{commentId}



        comment_id = #{commentId} and user_id = #{userId}

mapper.select:
select *
from forum_likes
where comment_id = #{commentId} and user_id = #{userId}

mapper.update:
update forum_comments



            like_count = like_count + 1, update_time = #{updateTime}



            like_count = like_count - 1, update_time = #{updateTime}



where comment_id = #{commentId}
