mapper.select:
select user_id, title, content, like_count, comment_count, state, create_time
from forums
where forum_id = #{forumId}

mapper.update:
update forums
set title = #{title}, content = #{content}, update_time = #{updateTime}
where forum_id = #{forumId}

update forums
set state = "Deleted", update_time = #{updateTime}
where forum_id = #{forumId}
